mapper.update:
update pay_order
set status = #{status}, update_time = now(), pay_url = #{payUrl},
market_type = #{marketType},market_deduction_amount = #{marketDeductionAmount},pay_amount = #{payAmount}
where order_id = #{orderId}

update pay_order
set status = #{status}, update_time = now(), pay_time = now()
where order_id = #{orderId}

update pay_order
set status = 'CLOSE', update_time = now()
where order_id = #{orderId}

update pay_order set status = 'MARKET', update_time = now()
where order_id in

    #{orderId}

mapper.select:
select user_id, product_id, product_name, order_id, order_time, total_amount, status, pay_url, market_type, market_deduction_amount, pay_amount
from pay_order
where order_id = #{orderId}

select id, user_id, product_id, product_name, order_id, order_time, total_amount, status, pay_url, pay_time, market_type, market_deduction_amount, pay_amount
from pay_order
where user_id = #{userId}

    and id > #{lastId}

order by id asc
limit #{pageSize}

select id, user_id, product_id, product_name, order_id, order_time, total_amount, status, pay_url, pay_time, market_type, market_deduction_amount, pay_amount
from pay_order
where user_id = #{userId} and order_id = #{orderId}

mapper.update:
update pay_order
set status = 'CLOSE', update_time = now()
where user_id = #{userId} and order_id = #{orderId}
